import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { styled } from "styled-components";
import { signup } from "../apis";

const SignUp = styled.section`
    display: flex;
    justify-content: center;
    align-items: center;
    min-height:100vh;
    background: #1c2630;

    > form {
        background: #29313c;
        padding: 60px 80px 60px;
        display: flex;
        flex-direction: column;
        border-radius: 10px;

        > h2 {
            color: #fff;
            font-weight: 500;
            text-transform: uppercase;
            font-size: 1.25em;
            letter-spacing: 0.1em;
            margin-bottom: 50px;
        }

        > .inputBox {
            position: relative;
            width: 300px;
            margin-bottom: 40px;

            > input {
                position: relative;
                width: 100%;
                padding: 10px 0;
                background: transparent;
                border: none;
                border-bottom: 2px solid #999;
                outline: none;
                color: #fff;
                font-size: 1em;
                text-transform: uppercase;
                letter-spacing: 0.15em;

                &:focus, &:valid {
                    border-bottom: 2px solid #0ab3ff;
                }
            }

            > label {
                position: absolute;
                left: 0;
                padding: 10px 0;
                pointer-events: none;
                color: #999;
                text-transform: uppercase;
            }

            > button {
                position: relative;
                width: 100%;
                padding: 15px;
                border-bottom: 2px solid #999;
                outline: none;
                color: #fff;
                text-transform: uppercase;
                letter-spacing: 0.15em;
                background: #0ab3ff;
                border: none;
                border-radius: 50px;
                color: #1c2630;
                font-weight: 600;
                font-size: 1.15em;

            }
        }

    }
`

export default function SignUpPage() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [validate, setValidate] = useState(false);
    const navigate = useNavigate();

    // 회원가입
    const signupHandler = async (event: any) => {
        event.preventDefault()
        const response = await signup(email, password)

        // 회원가입 성공한 경우, 로그인 페이지로 이동
        if (response?.status === 201) {
            navigate('/signin')
        }
    }

    // validate
    // ID @ 포함 & PW 8자 이상
    useEffect(() => {
        (email.includes("@") && password.length >= 8) ? setValidate(true) : setValidate(false)
    }, [email, password])

    // 로그인 여부에 따른 리다이렉트 - 토큰이 있으면 TODO 로 이동
    useEffect(() => {
        const token = localStorage.getItem("access_token")
        if (token) navigate("/todo")
    }, [navigate])

    return (
        <SignUp>
            <form onSubmit={signupHandler}>
                <h2>Sign Up</h2>
                <div className="inputBox">
                    <input type={"text"} required data-testid="email-input" onChange={(e) => setEmail(e.target.value)} />
                    <label>Email</label>
                </div>
                <div className="inputBox">
                    <input type={"password"} required data-testid="password-input" onChange={(e) => setPassword(e.target.value)} />
                    <label>Password</label>
                </div>
                <div className="inputBox">
                    {validate
                        ? <button data-testid="signup-button">Sign</button>
                        : <button disabled data-testid="signup-button">Sign</button>}
                </div>
                <p>Already have an account? <a href="/signin">Sign In</a></p>
            </form>
        </SignUp>
    )
}
