import axios from 'axios'

axios.defaults.baseURL = process.env.REACT_APP_SERVER_URL

// 1. Auth 관련
// 1-1. 회원가입
export const signup = async (email : String, password : String) => {
    try{
        const response = await axios.post('auth/signup', {email, password}, {headers: {"Content-Type": "application/json"}})
        const data = response

        return data
    }
    catch(error){
        window.alert(`회원 가입 불가 !\n 계정 정보를 확인해주세요`)
    }
}

// 1-2. 로그인
export const signin = async (email: string, password: string) => {
    try{
        const response = await axios.post('auth/signin', {email, password}, {headers: {"Content-Type": "application/json"}})
        const data = response.data.access_token

        return data
    }
    catch(error){
        window.alert(`로그인 실패 !\n 계정 정보를 확인해주세요`)
    }
}

// 2. ToDo 관련
// create ToDO

interface CreateToDo {
    token : string;
    id: number;
    todo: string;
    isCompleted: boolean;
    userId: number;
}

export const createTodo = async ({token, id, todo, isCompleted, userId}: CreateToDo) => {
    const data = {id, todo, isCompleted, userId}
    try{
        const response = await axios.post('/todos', data, {headers: {"Authorization": token, 'Content-Type': 'application/json'}})
        console.log(response)
    }
    catch(error){
        window.alert(`Something Wrong... Check Again!`)
    }
}
