import { useEffect, useRef, useState } from "react";
import { signup } from "../apis";

const SignUp = () => {
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [validate, setValidate] = useState(false);

    const submitHandler = (event: any) => {
        event.preventDefault()

        console.log(email, password)
    }

    useEffect(() => {
        (email.includes("@") && password.length >= 8) ? setValidate(true) : setValidate(false)
    }, [email, password])

    return (
        <form onSubmit={submitHandler}>signup
            <input data-testid="email-input" onChange={(e) => setEmail(e.target.value)} />
            <input data-testid="password-input" onChange={(e) => setPassword(e.target.value)} />
            {validate ? <button style={{ height: "20px" }} data-testid="signup-button">Sign</button> : <button disabled style={{ height: "20px" }} data-testid="signup-button">Sign</button>}

        </form>
    )
}

export default SignUp;

